#Tables

CREATE TABLE COUNTRY (
                         COUNTRY   VARCHAR(15) NOT NULL,
                         CURRENCY  VARCHAR(10) NOT NULL
);



CREATE TABLE CUSTOMER (
                          CUST_NO         INTEGER NOT NULL AUTO_INCREMENT PRIMARY KEY,
                          CUSTOMER        VARCHAR(25) NOT NULL,
                          CONTACT_FIRST   VARCHAR(15),
                          CONTACT_LAST    VARCHAR(20),
                          PHONE_NO        VARCHAR(20),
                          ADDRESS_LINE1   VARCHAR(30),
                          ADDRESS_LINE2   VARCHAR(30),
                          CITY            VARCHAR(25),
                          STATE_PROVINCE  VARCHAR(15),
                          COUNTRY         VARCHAR(15),
                          POSTAL_CODE     VARCHAR(12),
                          ON_HOLD         CHAR(1)
);



CREATE TABLE DEPARTMENT (
                            DEPT_NO     CHAR(3) NOT NULL,
                            DEPARTMENT  VARCHAR(25) NOT NULL,
                            HEAD_DEPT   CHAR(3),
                            MNGR_NO     SMALLINT,
                            BUDGET      DECIMAL(12,2),
                            LOCATION    VARCHAR(15),
                            PHONE_NO    VARCHAR(20) DEFAULT '555-1234'
);

CREATE TABLE EMPLOYEE (
                          EMP_NO       SMALLINT NOT NULL AUTO_INCREMENT PRIMARY KEY,
                          FIRST_NAME   VARCHAR(15) NOT NULL,
                          LAST_NAME    VARCHAR(20) NOT NULL,
                          PHONE_EXT    VARCHAR(4),
                          HIRE_DATE    DATE NOT NULL DEFAULT CURRENT_DATE,
                          DEPT_NO      CHAR(3) NOT NULL,
                          JOB_CODE     VARCHAR(5) NOT NULL,
                          JOB_GRADE    SMALLINT NOT NULL,
                          JOB_COUNTRY  VARCHAR(15) NOT NULL,
                          SALARY       NUMERIC(18,2) NOT NULL,
                          FULL_NAME    VARCHAR(50) GENERATED ALWAYS AS (CONCAT(LAST_NAME,  ', ', FIRST_NAME))
);



CREATE TABLE EMPLOYEE_PROJECT (
                                  EMP_NO   SMALLINT NOT NULL,
                                  PROJ_ID  CHAR(5) NOT NULL
);


CREATE TABLE JOB (
                     JOB_CODE         VARCHAR(5) NOT NULL,
                     JOB_GRADE        SMALLINT NOT NULL,
                     JOB_COUNTRY      VARCHAR(15) NOT NULL,
                     JOB_TITLE        VARCHAR(25) NOT NULL,
                     MIN_SALARY       NUMERIC(18,2) NOT NULL,
                     MAX_SALARY       NUMERIC(18,2) NOT NULL,
                     JOB_REQUIREMENT  BLOB ,
                     LANGUAGE_REQ     VARCHAR(15)
);



CREATE TABLE PROJ_DEPT_BUDGET (
                                  FISCAL_YEAR       INTEGER NOT NULL,
                                  PROJ_ID           CHAR(5) NOT NULL,
                                  DEPT_NO           CHAR(3) NOT NULL,
                                  QUART_HEAD_CNT    INTEGER,
                                  PROJECTED_BUDGET  DECIMAL(12,2)
);



CREATE TABLE PROJECT (
                         PROJ_ID      CHAR(5) NOT NULL,
                         PROJ_NAME    VARCHAR(20) NOT NULL,
                         PROJ_DESC    BLOB,
                         TEAM_LEADER  SMALLINT,
                         PRODUCT      VARCHAR(12)
);



CREATE TABLE SALARY_HISTORY (
                                EMP_NO          SMALLINT NOT NULL,
                                CHANGE_DATE     DATE NOT NULL, #DEFAULT CURRENT_DATE()
                                UPDATER_ID      VARCHAR(20) NOT NULL,
                                OLD_SALARY      NUMERIC(18,2) NOT NULL,
                                PERCENT_CHANGE  DOUBLE DEFAULT 0 NOT NULL,
                                NEW_SALARY      NUMERIC(18,2) GENERATED ALWAYS AS (ROUND(OLD_SALARY + OLD_SALARY * PERCENT_CHANGE / 100, 2))
);



CREATE TABLE SALES (
                       PO_NUMBER     CHAR(8) NOT NULL,
                       CUST_NO       INTEGER NOT NULL,
                       SALES_REP     SMALLINT,
                       ORDER_STATUS  VARCHAR(7) DEFAULT 'new' NOT NULL,
                       ORDER_DATE    DATE NOT NULL DEFAULT CURRENT_DATE,
                       SHIP_DATE     DATE,
                       DATE_NEEDED   DATE,
                       PAID          CHAR(1) DEFAULT 'n',
                       QTY_ORDERED   INTEGER DEFAULT 1 NOT NULL,
                       TOTAL_VALUE   DECIMAL(9,2) NOT NULL,
                       DISCOUNT      FLOAT DEFAULT 0 NOT NULL,
                       ITEM_TYPE     VARCHAR(12),
                       AGED          NUMERIC(18,9) GENERATED ALWAYS AS (DATEDIFF(SHIP_DATE,ORDER_DATE))
);




#Views


CREATE VIEW PHONE_LIST(
                       EMP_NO,
                       FIRST_NAME,
                       LAST_NAME,
                       PHONE_EXT,
                       LOCATION,
                       PHONE_NO)
AS
SELECT
    emp_no, first_name, last_name, phone_ext, location, phone_no
FROM employee, department
WHERE employee.dept_no = department.dept_no
;


