delimiter |

CREATE TRIGGER SET_SALARY_CHANGE AFTER UPDATE ON employee
    FOR EACH ROW BEGIN
    IF NEW.SALARY!=OLD.SALARY THEN
        VALUES (NEW.EMP_NO, CURRENT_DATE,  CURRENT_USER, OLD.SALARY, (NEW.SALARY-OLD.SALARY)*100/OLD.SALARY);
    END IF;
END;
|

